/**
 * Delegate event
 */
type DelegateEvent = Event & {
    delegateTarget: Element;
};
/**
 * Delegate callback
 */
type DelegateCallback = (e: DelegateEvent) => void;
/**
 * Delegate arguments
 */
type DelegateArguments = [Element, string, string, DelegateCallback];
/**
 * Delegates event to a selector.
 *
 * @param {Element|string|Array} [elements]
 * @param {string | Element} selector
 * @param {string} type
 * @param {Function} callback
 * @param {boolean} useCapture
 * @return {Object}
 * @example
 * ```ts
 * const delegation = delegate(document.body, 'a', 'click', function (e) {})
 * // destroy (removeEventListener)
 * delegation.destroy();
 *
 * const delegation1 = delegate(".btn", "click", function (e) { console.log(e.delegateTarget)},false);
 * // destroy (removeEventListener)
 * delegation1.destroy();
 * ```
 */
declare function delegate(elements: Element | string | Element[], selector: string, type: string, callback: DelegateCallback, useCapture?: boolean): any;

export { delegate as default };
export type { DelegateArguments, DelegateCallback, DelegateEvent };
